import React, { Component } from 'react';
import {storeProducts, detailProduct} from './data';


const ProductsContext = React.createContext();



class ProductsProvider extends Component {
    state = {
        products: [],
        detailsProducts:detailProduct
    };

    componentDidMount(){
        this.setProducts();
    }
    setProducts = () => {
        let products = [];
        storeProducts.forEach(item => {
            const singleItem = {...item};
            products = [...products, singleItem];
        });
        this.setState(() => {
            return {products};
        });
    }



    handelDetail = () =>{
        console.log("this is for handel detail function")
    }
    addToCart = () =>{
        console.log("this is for addToCart function")
    }
    render() {
        return (
            <ProductsContext.Provider value={{
                ...this.state,
                handelDetail:this.handelDetail,
                addToCart:this.addToCart
            }}>
                {this.props.children}
            </ProductsContext.Provider>
        );
    }
}


// product consumer

const ProductsConsumer = ProductsContext.Consumer;

export  {ProductsProvider, ProductsConsumer};
